}

class HomePage extends StatelessWidget {
  const HomePage({Key? key}) : super(key: key);

  Future<void> _signOut(BuildContext context) async {
    try {
      await FirebaseAuth.instance.signOut();
      // AuthGate will redirect to SignIn
    } catch (e) {
      ScaffoldMessenger.of(context).showSnackBar(
        const SnackBar(content: Text('Sign out failed')),
      );
    }
  }

  @override
  Widget build(BuildContext context) {
    final user = FirebaseAuth.instance.currentUser;

    return Scaffold(
      appBar: AppBar(
        title: const Text('Home'),
        actions: [
          IconButton(
            tooltip: 'Sign out',
            onPressed: () => _signOut(context),
            icon: const Icon(Icons.logout),
          ),
        ],
      ),
      body: Center(
        child: Padding(
          padding: const EdgeInsets.all(20.0),
          child: Column(
            mainAxisSize: MainAxisSize.min,
            children: [
              Text('Hello, ${user?.email ?? 'Anonymous user'}', style: const TextStyle(fontSize: 18)),
              const SizedBox(height: 12),
              Text('UID: ${user?.uid ?? 'N/A'}'),
              const SizedBox(height: 20),
              ElevatedButton(
                onPressed: () async {
                  // Example: send email verification
                  if (user != null && !user.emailVerified && user.email != null) {
                    await user.sendEmailVerification();
                    ScaffoldMessenger.of(context).showSnackBar(const SnackBar(content: Text('Verification email sent')));
                  }
                },
                child: const Text('Send verification email (if applicable)'),
              ),
            ],
          ),
        ),
      ),
    );
  }
}

